select * from(select s_.*,rownum rn_ from(
select distinct
f.*,count(distinct f.id) over () tot_cnt
from FILE_INFO f
inner join
File_Auth auth
on f.id = auth.file_id

    ((

        auth.OPER_ID = #{operId,jdbcType=VARCHAR}


        auth.ORG_C_DATA = #{orgCData,jdbcType=VARCHAR}


        auth.ORG_S_DATA = #{orgSData,jdbcType=VARCHAR}


        auth.ORG_T_DATA = #{orgTData,jdbcType=VARCHAR}



    ) or (f.HOSTER_ID = #{zbrId,jdbcType=VARCHAR}) )

    and f.STATUS NOT LIKE '9%'


        and f.status = #{status,jdbcType=VARCHAR}


        and f.CODE like #{ajbh,jdbcType=VARCHAR}


        and f.KEYWORDS like #{saryxm,jdbcType=VARCHAR}


        and f.NAME like #{ajmc,jdbcType=VARCHAR}


        and f.SPACE_ID = #{spaceId,jdbcType=VARCHAR}


        and f.HOSTER_ID = #{hosterId,jdbcType=VARCHAR}


        and f.HANDLE_ORG_CODE =#{handleOrgCode,jdbcType=VARCHAR}


        and f.type = #{type,jdbcType=VARCHAR}


        and f.CREATE_TIME >= #{createTimeq,jdbcType=VARCHAR}


        and f.CREATE_TIME <=#{createTimej,jdbcType=VARCHAR}


order by f.MOD_TIME DESC
) s_ where rownum <=#{start,jdbcType=DECIMAL}+20) where rn_ >#{start,jdbcType=DECIMAL}

select * from(select s_.*,rownum rn_ from(
select distinct
f.*,count(*) over () tot_cnt
from
FILE_INFO f,File_Auth auth,file_store store

    f.id = auth.file_id and f.id = store.file_id
    and store.ROW_STATUS = 3

        and auth.OPER_ID = #{operId,jdbcType=VARCHAR}


        and auth.ORG_C_DATA = #{orgCData,jdbcType=VARCHAR}


        and NVL(auth.ORG_S_DATA, '<Null>') = #{orgSData,jdbcType=VARCHAR}


        and NVL(auth.ORG_T_DATA, '<Null>') = #{orgTData,jdbcType=VARCHAR}


        and store.ORG_C_DATA = #{orgCData,jdbcType=VARCHAR}


        and NVL(store.ORG_S_DATA, '<Null>') = #{orgSData,jdbcType=VARCHAR}


        and NVL(store.ORG_T_DATA, '<Null>') = #{orgTData,jdbcType=VARCHAR}


    and
    (
    (f.STATUS = #{status1,jdbcType=VARCHAR} and auth.STATUS = #{operType1,jdbcType=VARCHAR} and auth.Oper_Type
    != '0100')
    or
    (f.STATUS = #{status2,jdbcType=VARCHAR} and auth.STATUS = #{operType2,jdbcType=VARCHAR})
    or
    (f.STATUS = #{status3,jdbcType=VARCHAR} )
    or
    (f.STATUS = #{status4,jdbcType=VARCHAR} )
    or
    (f.STATUS = #{status5,jdbcType=VARCHAR} )
    or
    (f.STATUS = #{status6,jdbcType=VARCHAR} )
    or
    (f.STATUS = #{yzf,jdbcType=VARCHAR} )
    )

        and f.CODE like #{ajbh,jdbcType=VARCHAR}


        and f.NAME like #{ajmc,jdbcType=VARCHAR}


        and f.id in
        ( select store.file_id from file_store store
        where
        store.metal_id='00000000-0000-0000-0000-000000000000'
        and store.page_id='00000000-0000-0000-0000-000000000000'
        and store.space_id=#{spaceId,jdbcType=VARCHAR}
        and store.ROW_STATUS = 3
        )


        and f.HOSTER_ID = #{hosterId,jdbcType=VARCHAR}


        and f.HANDLE_ORG_CODE = #{handleOrgCode,jdbcType=VARCHAR}


order by f.CREATE_TIME desc
) s_ where rownum <=#{start,jdbcType=DECIMAL}+20) where rn_ >#{start,jdbcType=DECIMAL}


