where

    auth.OPER_ID=#{operId,jdbcType=VARCHAR}


    auth.ORG_C_DATA = #{orgCData,jdbcType=VARCHAR}


    auth.ORG_S_DATA = #{orgSData,jdbcType=VARCHAR}


    auth.ORG_T_DATA = #{orgTData,jdbcType=VARCHAR}


    and Upper(f.CODE) = #{ajbh,jdbcType=VARCHAR}


    and Upper(f.NAME) like #{ajmc,jdbcType=VARCHAR}


    and f.KEYWORDS like #{saryxm,jdbcType=VARCHAR}


    and f.HOSTER_ID = #{hosterId,jdbcType=VARCHAR}


    and f.HANDLE_ORG_CODE =#{handleOrgCode,jdbcType=VARCHAR}



and f.STATUS NOT LIKE '9%'

and f.STATUS != #{status,jdbcType=VARCHAR}
and f.STATUS != #{status1,jdbcType=VARCHAR}
order by f.MOD_TIME DESC
) s_ where rownum <=#{start,jdbcType=DECIMAL}+20) where rn_ >#{start,jdbcType=DECIMAL}

